INSERT INTO `Wonderful_Places` (`n`, `name`, `description`, `fstate`, `active`) VALUES
(1, 'آرامگاه سعدی', 'آرامگاه سعدی معروف به سعدیه محل زندگی و دفن سعدی، شاعر برجستهٔ پارسی‌گوی است.<br><br>این آرامگاه در انتهای خیابان بوستان و کنار باغ دلگشا در دامنهٔ کوه در شمال شرق شیراز قرار دارد. در اطراف مقبره، قبور زیادی از بزرگان دین وجود دارند که بنا به وصیت خود، در آنجا مدفون شده‌اند. از جمله مهمترین‌های آن می‌توان شوریده شیرازی را نام برد که آرامگاهش به وسیله رواق به آرامگاه سعدی متصل شده‌است', 17, 1),
(2, 'آبشار مارگون', 'آبشار مارگون (انگلیسی: Margoon Waterfall) یکی از زیباترین آبشارهای کشور ایران می‌باشد و در شهرستان سپیدان استان فارس، دهستان مارگون واقع گردیده است. این آبشار در حقیقت سرچشمه رودخانه است و در بالای کوه هیچ رودخانه‌ای نیست، بلکه از بدنه دیواره صخره‌ای کوه، بیش از چند هزار چشمه وجود دارد که آب از آنها به بیرون ریخته می‌شود. از این حیث این آبشار هم بزرگترین و هم مرتفع‌ترین آبشار چشمه‌ای در جهان است.', 17, 1),
(3, 'تخت جمشید', 'تخت جمشید یا پارسه، نام یکی از شهرهای باستانی ایران است که طی سالیان پیوسته، پایتخت باشکوه و تشریفاتیِ پادشاهی ایران در زمان امپراتوری هخامنشیان بوده‌است. در این شهر باستانی کاخی به نام تخت جمشید وجود دارد که در دوران زمامداری داریوش بزرگ، خشایارشا و اردشیر اول بنا شده‌است و به مدت حدود ۲۰۰ سال آباد بوده‌است.<br>۵۱۸ سال پیش از میلاد مسیح، بنای تخت جمشید به عنوان پایتخت جدید هخامنشیان در پارسه آغاز گردید. بنیانگذار تخت جمشید داریوش بزرگ بود، البته پس از او پسرش خشایارشا و نوه‌اش اردشیر یکم با گسترش این مجموعه به آن افزودند.<br>مجموعه کاخ های تخت جمشید، سرانجام 313 سال پیش از میلاد، توسط اسکندر کبیر به آتش کشیده شد.', 17, 1),
(4, 'حافظیّه', 'حافظیه یا آرامگاه حافظ نام مجموعه‌ای آرامگاهی در شمال شهر شیراز و در جنوب دروازه قرآن است که آرامگاه حافظ شیرازی را در خود جای داده است. ', 17, 1),
(5, 'ارگ کریم خانی', 'ارگ کریم خانی در مرکز شهر شیراز قرار دارد. این ارگ در دوره سلطنت سلسله زندیه ساخته شده‌است و پس از اینکه کریم خان زند شیراز را به عنوان پایتخت خود و این مکان را به‌عنوان مکان زندگی خود انتخاب کرد، به ارگ کریم خانی معروف شد.', 17, 1),
(6, 'دروازه قرآن', 'دروازه قرآن یکی از دروازه‌های به جای مانده از دوره‌های قدیم در شیراز است که امروزه به عنوان یکی از آثار تاریخی این شهر به حساب می‌آید.<br>این دروازه در ابتدا در زمان عضدالدوله دیلمی ساخته شد و قرآنی در آن جای داده شد تا مسافران با گذر از زیر آن متبرک شوند. در دوره زندیه کریم خان زند این دروازه را بازسازی کرد و اتاقی به بالای آن افزود و دو جلد قرآن بزرگ نفیس، به خط سلطان ابراهیم بن شاهرخ تیموری، در اتاقک بالای آن جای داد. این بنا در گذشته طاق قرآن نیز نامیده شده‌است. روزهای اول هرماه مردم از شهر خارج شده و از زیر این طاق عبور می‌کرده‌اند. درسال ۱۳۱۵ این بنا توسط شهردار وقت شیراز تخریب شد، هر چند بعداً مرمّت و بازسازی شد.<br>', 17, 1),
(7, 'باغ دلگشا', 'باغ دلگشا باغی مربوط به دوره ساسانیان، صفویه و قاجار است که در شیراز جای گرفته است.<br>این باغ در بخش شمال شرقی شهر شیراز در ضلع جنوبی تنگ آب خان و در دامنه کوهستان قرار دارد. این باغ در کنار قنات سعدی و در منطقه دژی به نام کهندژ جای گرفته است و فاصله آن تا آرامگاه سعدی چند گامی بیشتر نیست.<br>عمارت باغ به شیوه معماری کاخ‌های ساسانی بیشاپور ساخته شده است که از یک تالار با چهار شاه‌نشین شکل گرفته است. این عمارت دارای سه طبقه و یک ایوان با دو ستون است. ساخت این عمارت در دوره زندیه انجام گرفته است. این عمارت دارای آینه‌کاری‌هایی به همراه نقاشی‌های رنگ روغن با سقف‌هایی از چوب و گچبری‌هایی آراسته به نقش گل و بته است.<br>پیشینه آبادانی و دیرینگی این باغ به دوران پیش از اسلام و زمان فرمانروایی ساسانیان می‌رسد. در فاصله دوره نادرشاه تا دودمان زندیه این باغ آسیب‌های فراوانی به خود دید و در زمان کریمخان زند آباد شد. همچنین در این باغ عمارت‌هایی مربوط به دوره قاجاریه دیده می‌شود.', 17, 1),
(8, 'باغ ارم', 'باغ ارم یک باغ ایرانی تاریخی در شهر شیراز است و شامل چند بنای تاریخی و باغ گیاه‌شناسی می‌شود.<br>اریخ ساخت و بنیان‌گذار اولیه باغ ارم شیراز، به‌درستی مشخص نیست؛ ولی توصیف‌هایی از آن در سفرنامه‌های متعلق به قرن دهم و یازدهم هجری آمده‌است. این باغ در روزگار سلجوقیان و آل اینجو پابرجا بوده است. در زمان زندیه هم کریم‌خان زند در سازندگی و بهسازی این باغ کوشید. در زمان قاجاریه این باغ به مدت ۷۵ سال به دست سران ایل قشقایی افتاد. در این زمان عمارتی در این باغ ساخته شد.<br>این باغ تنوع گیاهی بسیار بالایی دارد و گیاهان بسیاری از اقصا نقاط جهان در این باغ کاشته شده است؛ به شکلی که باغ در قالب یک نمایشگاه از انواع گل‌ها و گیاهان درآمده‌است. در حال حاضر این باغ در اختیار دانشگاه شیراز است؛ باغ گیاه‌شناسی آن در اختیار دانشکده کشاورزی و ساختمان باغ در اختیار دانشکده حقوق قرار دارد. در تاریخ ۶ تیرماه ۱۳۹۰، در سی‌وپنجمین اجلاس کمیتهٔ میراث جهانی یونسکو باغ ارم شیراز به همراه هشت باغ دیگر ایرانی در فهرست میراث جهانی ثبت گردید.', 17, 1),
(10, 'جنگل ارسباران', 'جنگل ارسباران یکی از مناطق بکر و دست نخورده تبریز است. جنگلی سرسبز که انواع گونه های جانوری و گیاهی را در دل خود جای داده است. این جنگل به نام های ارسبار یا آرازبار و قره داغ یا قراچه داغ هم شناخته می شود.<br>جنگل های ارسباران با طبیعت زیبا و چشم اندازهای دلنشین و وجود اماکن و ابنیه تاریخی، توان بالقوه بالایی برای جذب گردشگر دارد در واقع این جنگل یکی از جاذبه های گردشگری تبریز است که هواداران زیادی دارد.', 1, 1),
(11, 'کلیسای کاتولیک ها', 'کلیسای کاتولیک ها (عذرای توانا) متعلق به مسیحیان کاتولیک ایران است. این بنا در سال ۱۲۹۱ خورشیدی احداث شده و نمای آن آجری می باشد. طول کلیسای کاتولیک ها ۳۰ متر و عرض آن ۱۵ متر است و برج ناقوس کلیسا در بالای بالکن کوچکی قرار گرفته است.<br>این کلیسا برای اجرای مراسم و جشن های کاتولیک ها مورداستفاده قرار می گیرد.<br>', 1, 1),
(12, 'دریاچه ارومیه', 'دریاچه ارومیه نام دریاچه‌ای در شمال غربی کشور ایران است که میان دو استان آذربایجان غربی و استان آذربایجان شرقی قرار گرفته‌است.<br>مساحت این دریاچه در تابستان ۲۰۱۵ در حدود شش هزار کیلومتر مربع بود که در ردیف بیست و پنجمین دریاچه بزرگ دنیا از نظر مساحت قرار می‌گیرد.<br>دریاچهٔ ارومیه، بزرگ‌ترین دریاچهٔ داخلی ایران و دومین دریاچهٔ بزرگ آب شور دنیا است. آب این دریاچه بسیار شور بوده و بیشتر از رودخانه‌های زرینه‌رود، سیمینه‌رود، تلخه رود، گادر، باراندوز، شهرچای، نازلو و زولا تغذیه می‌شود.', 2, 1),
(13, 'تپه حسنلو', 'تپه حسنلو که در ۷ کیلومتری شهر نقده قرار دارد، یکی از تپه‌های باستانی ایران است که قدمت آن به بیش از ۶ هزار سال قبل از میلاد می‌رسد. معروفترین اثر باستانی یافت شده در این محل جام طلای حسنلو است که به عصر آهن تعلق داشته و در موزه ایران باستان نگهداری می‌شود.<br>تپه باستانی حسنلو در ۱۲ کیلومتری جنوب غربی دریاچه ارومیه، و ۹ کیلومتری شمال شرقی شهرستان نقده بین دهکده‌های امین‌لو و حسنلو واقع شده‌است. این تپه به خاطر نام دهکده مجاورش حسنلو نام گرفته‌است.', 2, 1),
(14, 'تخت سلیمان', 'تخت سلیمان (یا آتشکده آذرگُشنَسب)، نام محوطه تاریخی بزرگی در نزدیکی تکاب و روستای تخت سلیمان (در گذشته نصرت‌آباد) در استان آذربایجان غربی و ۴۵ کیلومتری شمال شرقی این شهر است. تخت سلیمان بزرگ‌ترین مرکز آموزشی، مذهبی، اجتماعی و عبادتگاه ایرانیان در قبل از اسلام به شمار می‌رفت؛ اما در سال ۶۲۴ میلادی و در حمله هراکلیوس، امپراتور رومیان، به ایران تخریب شد. ', 2, 1),
(15, 'کوه سبلان', 'کوه سبلان در 35 کیلومتری غرب شهر اردبیل و 25 کیلومتری جنوب شرقی شهرستان مشگین‌شهر واقع است. این کوه در مرز بین بخش مرکزی شهرستان اردبیل و بخش لاهرود شهرستان مشگین شهر واقع می باشد.<br>مخروط آتشفشانی سبلان از نوع چینه ای است که گدازه های آن سطحی معادل 1200 کیلومتر مربع را اشغال کرده اند. مخروط سبلان ساختمان مرکزی عظیمی است که بر روی یک سیستم هورست با روند شرقی - غربی قرار گرفته است.<br>سبلان سومین قله بلند ایران پس از دماوند و علم کوه و یک کوه آتشفشانی غیرفعال است. ارتفاع قله این کوه ۴۸۱۱ متر است و در بالای قله آن دریاچه کوچکی قرار دارد. سبلان به خاطر آبگرم‌های طبیعی دامنه کوه، طبیعت تابستانی زیبا و پیست اسکی آلوارس مورد توجه گردشگران است. همچنین برخی کوه سبلان را محل بعثت پیامبر ایران، زرتشت، می دانند و از این نظر مکانی مقدس و مورد احترام است که حتی در میان بومیان و عشایر منطقه به نام آن قسم یاد می شود.<br>کوهستان سبلان به طور کلی سه قله معروف دارد، قله بزرگ آن به سلطان مشهور است و دو قله دیگر آن به هرم و کسری مشهورند. کوه سبلان از مجموعه‌ای از ارتفاعات متعدد تشکیل شده که به موازات ارسباران ولی اندکی در شرق آن کشیده شده‌ است. امتداد آن شرقی - غربی است و از شرق و شمال و جنوب کوهی به نام قوشه داغ آنرا به رشته کوه ارسباران متصل می‌کند. در جنوب آن کوه بزقوش، که از سمت جنوب غربی به کوهستان سهند مربوط است، کشیده شده‌ است. قسمت شرقی کوه سبلان به قله آتشفشان سبلان که در بلندای ۴۸۱۱ متری قرار دارد، منتهی می‌شود. تمام قله‌های سبلان در همه ایام سال پوشیده از یخ و برف‌های دائمی هستند.', 3, 1),
(16, 'پیست اسکی آلوارس', 'یست اسکی آلوارس، بزرگترین پیست اسکی از لحاظ وسعت در ایران، در دامنه های مرفتفع سبلان واقع شده است.<br>پیست اسکی آلوارس در فاصله ۱۲ کیلومتری روستای آلوارس و ۲۴ کیلومتری سرعین واقع شده و از آنجایی که در ارتفاع ۳۲۰۰ متری از سطح دریا قرار دارد و به جهت دیر ذوب شدن برف های این منطقه می تواند در حدود شش الی هشت ماه از سال مورد استفاده قرار گیرد؛ که از این لحاظ در کشور در رتبه اول است.', 3, 1),
(17, 'جنگل فندقلو', 'جنگل فندقلو در 30 کیلومتری شهرستان اردبیل و 10 کیلومتری جنوب شهر نمین واقع شده ‌است.<br>جنگل فندقلو بخاطر داشتن جاذبه‌ های منحصر به فرد جنگلی - مرتعی و گردنه زیبای حیران، قابلیتی بسیار ممتاز برای توسعه در صنعت اکوتوریسم دارد.<br>عرصه جنگلی فندقلو بخشی از جنگل‌های شرق استان اردبیل به شمار می‌ رود که دنباله جنگل‌ های نیمه گرمسیری استان گیلان می‌ باشد. مساحت منطقه فندقلو حدود 16 هزار هکتار است که بیش از چهار هزار هکتار آن تحت پوشش عرصه‌ های جنگلی قرار دارد و بقیه را مراتع تشکیل می‌ دهد.<br>جنگل فندقلو استعداد پرورش صد نوع گل از جمله بوماداران و بابونه را دارند. 19 گونه جنگلی متعلق به 8 تیره و 17 جنس شناسایی شده است. در بین انواع شناسایی شده گونه‌های نادری نظیر فندق، ممرز، راش، بلوط (بلند مازو) و بید مشک وجود دارد و گونه‌های علفی منطقه چون نسترن، تمشک، خاس، پامچال، بابونه، سرخس، گیلدیگ، بنفشه وحشی، گل گاو زبان و … در زمین های آن به وفور یافت می‌ شود. با توجه به اینکه درخت فندق بیش از سایر گونه‌ها در این جنگل روییده است، این منطقه فندق‌لو نامیده می‌ شود.<br>در منطقه فندقلو حیواناتی نظیر روباه، گرگ، خرس قهوه ای، سمور و پرندگانی چون قرقاول، کبک، عقاب طلایی، چکاوک و کلاغ ابلق یافت می‌شود.', 3, 1),
(18, 'آبگرم گاومیش گلی', 'آبگرم گاو میش گلی در شهر سرعین و در ارتفاع 1940 متری از سطح دریا واقع شده که پر آب ترین چشمه معدنی شهر سرعین از لحاظ آبدهی و وسعت می باشد و آبدهی آن 140 لیتر در ثانیه و دارای استخری روباز به مساحت ۴۰۰ متر با عمق ۱۳۰ سانتی متر که آب آن به صورت دائمی و جوشان در جریان است. دهانه آن پراکنده و دمای آب در حوض 46 درجه سانتی گراد است. آب گاومیش گلی ترش مزه بیرنگ و کمی بودار است. آنیونهای آن کربنات، بیکربنات، کلروره، سولفات و کاتیون های آن کلسیم، منیزیم، سدیم و پتایسم می باشد. <br>آبگرم گاو میش گلی به علت داشتن ترکیب کلروبی کربناته سدیک و کلسیک برای درمان بیماری های عمومی، بیماریهای سیستم حرکتی (دردهای مزمن روماتیسمی) و بیماری های زنان، خنازیر و بیماری های قلبی و نیز تقویت عمومی بدن قابل استفاده است.<br>', 3, 1),
(19, 'پارک جنگلی مشگین شهر', 'پارک جنگلی مشگین شهر از جمله مکان های بسیار زیبا و دیدنی و محلی مناسب برای گردش و تفریح می‌باشد. وجود قهوه‌خانه‌های سنتی از مشخصه این پارک می‌باشد که هیچ یک از وسایلی که در پارک های معمولی وجود دارد در آن دیده نمی‌شود و پارک طبیعت است. این پارک در بالادست رودخانه خیاوچای قراردارد.<br>پارک جنگلی مشکین شهر که در حاشیه رودخانه خیاوچایی با انبوه درختان برافراشته و به دلیل مجاورت با شهر و برخورداری از طبیعت ناب و وجود میوه‌های گوناگون، صدای دلنشین نهرها و جریان آب پرخروشِ خیاوچای و زمزمه‌های بلبلان  هر روز میزبان علاقمندان زیادی است. وجود تفرجگاه، شهربازی، زمین اسکیت و آبشار مصنوعی در ورودی پارک بر زیبایی های این مکان افزوده و در اکثر فصول سال مملو از شهروندان و گردشگران می باشد.<br>این پارک با داشتن طبیعتی پاک، در فصول گرم سال محلی برای ورزش های صبحگاهی نیز به شمار می رود، به طوری که پیاده روی های خانوادگی نیز در زمانهای مختلف در این محل صورت می گیرد. این پارک معمولا شبانگاه هر روز و عصر روز پنجشنبه و جمعه محل استراحت و تفریحگاه اهالی منطقه می‌باشد و در ایام عید، خصوصا عید نوروز و سیزده‌بدر مملو از جمعیت است.', 3, 1),
(20, 'تپه باستانی سیلک', 'سیَلْک نام اولین تمدن شهرنشینی ایران مرکزی در کاشان است که در ۳ کیلومتری جنوب غربی این شهر قرار دارد. محوطه باستانی سیلک که در منطقه فین کاشان واقع شده قدمتی در حدود ۷ هزار سال دارد.<br>نزدیک به پنج هزار سال پیش از میلاد مردم غارنشین فلات ایران در پی دگرگونی‌هایی که از لحاظ آب و هوا و تشکیل مزارع و چمنزارها به وجود آمد به دشت‌ها روی آوردند و زندگی تازه‌ای را آغاز کردند و در تمدن آن‌ها نسبت به دوران‌های پیشین پیشرفت بیشتری دیده شد؛ کهن‌ترین مردم دشت‌نشین، مردم محل سیلک در نزدیکی کاشان بودند که آثار زندگی ایشان را در آن‌جا به دست آورده‌اند.<br>سیلک یکی از مکان‌های تاریخی پس از تمدن جیرفت کرمان در فلات ایران است که مدارک نوشته از دوران پیش از هخامنشی در آن پیدا شده ولی خطی که هم‌زمان با آغاز تاریخ در ایلام به وجود آمده‌بود، با نام پرتو ایلامی شناخته شده پس از رها شدن این پایگاه بازرگانی از میان رفت.<br>در هنر سیلک جنبه تزیینی آن شدید است و هنری است که آریایی‌ها با خود آورده‌اند.<br>در حدود ۵٬۵۰۰ سال پیش از میلاد یکی از قدیمی‌ترین سکونت‌گاه در تپه سیلک در نزدیکی کاشان پدید آمد.', 4, 1),
(21, 'عالی قاپو', 'کاخ عالی‌قاپو (در زبان ترکی به معنی درگاه بلند) ساختمانی است که در واقع درب ورودی دولتخانه صفوی بوده‌است و در ابتدا شکلی ساده داشته و به مرور زمان و در طول سلطنت شاه عباس طبقاتی به آن افزوده شدند و در زمان شاه عباس دوم ایوان ستوندار به آن افزوده شد.<br>این بنا در ضلع غربی میدان نقش جهان و روبروی مسجد شیخ لطف‌الله واقع شده‌است. ارتفاع آن ۴۸ متر است و ۶ طبقه دارد که با راه‌پله‌های مارپیچ می‌توان به آنها رسید. آنچه باعث گردیده است عالی قاپو در زمره آثار باشکوه و بسیار نفیس عصر صفوی قرار گیرد، مینیاتورهایی هست که کار هنرمند معروف عصر صفوی رضا عباسی است و همچنین گچبری‌های آخرین طبقه کاخ عالی قاپو است که تالار آن اتاق موسیقی یا اتاق صوت نیز نامیده می‌شود.', 4, 1),
(22, 'سی و سه پل', 'سی‌وسه‌پل یا پل الله‌وردی خان پلی است با ۳۳ دهانه، ۲۹۵ متر طول و ۱۴ متر عرض، که توسط الله‌وردی خان بر روی زاینده‌رود در شهر اصفهان و همزمان با حکومت شاه عباس صفوی ساخته شده و محل برگزاری مراسم جشن آب‌پاشان و همچنین مراسم خاج‌شویان ارامنه اصفهان در دورهٔ صفویه بوده است.<br>اندیشه بنای سی و سه پل در سال ۱۰۰۸ هجری قمری و در دوازدهمین سال سلطنت شاه عباس یکم به وجود آمد و در سال ۱۰۱۱ هجری قمری، الله وردی خان اوندیلادزه گرجی، سردار مشهور او مأمور اتمام ساختمان پل گردید.<br>در سفرنامه‌های سیاحان اروپایی آن دوران؛ به برگزاری این جشن اشاره‌هایی شده است. در این جشن که در ۱۳ تیرماه هر سال برگزار می‌شد مردم با پاشیدن آب و گلاب روی یکدیگر در این مراسم شرکت می‌کرده‌اند. ارامنه جلفای اصفهان هم مراسم خاج شویان خود را در محدوده همین پل برگزار می‌کرده‌اند. این پل یکی از شاهکارهای معماری و پل‌سازی ایران محسوب می‌شود و از زیبایی و عظمت منحصر به فردی برخودار است.', 4, 1),
(23, 'چهل ستون', 'کاخ چهل‌ستون از بناهای تاریخی اصفهان است. کاخ چهل ستون که بالغ بر ۶۷۰۰۰ متر مربع مساحت دارد، در دوره شاه عباس یکم احداث آن آغاز شد و در وسط آن عمارتی ساخته شده بود. شاه عباس اول، کوشکی درمیان باغ بنا نهاد که این کوشک هسته اولیه کاخ را تشکیل داد که تالار میانی کاخ چهل ستون و غرفه‌های چهار گوشه آن را در بر می‌گرفت.<br>در سلطنت شاه عباس دوم، ساختمان تکمیل شد و در ساختمان موجود مرکزی، تغییرات کلی داده شده‌است و تالار آینه، تالار ۲۰ ستون، دو اتاق بزرگ شمالی و جنوبی تالار آینه، ایوان های طرفین سالن پادشاهی و حوض بزرگ مقابل تالار با تمام تزیینات نقاشی و آئینه کاری و کاشی کاری دیوارها و سقف ها افزوده شده‌است.', 4, 1),
(24, 'منار جنبان', 'منارجنبان اصفهان یکی از آثار تاریخی این شهر است که عارفی به نام «عمو عبدالله کارلادانی» در آن به خاک سپرده شده‌است. نکته قابل توجه دربارهٔ این بنای تاریخی این است که با حرکت دادن یک مناره، مناره دیگر نیز به حرکت و جنب و جوش می‌آید و لازم است ذکر شود هر مناره دارای پهنای نه متری و بلندای هفده متری می‌باشد.<br>تاریخی که بر فراز سنگ عمو عبدالله نوشته شده است سال ۷۱۶ هجری را نشان می‌دهد که در دوره سلطان محمد خدابنده الجایتو ایلخان مسلمان است.', 4, 1),
(25, 'آبشار نیاسر', 'آبشار نیاسر کاشان از جمله زیباترین آبشارهای ایران است که در شهری تاریخی نیاسر در محله سر کمر (شهید بهشتی) قرار گرفته است.<br>آب این آبشار از چشمه تالار (اسکندریه) واقع در کنار چارتاقی سرچشمه می گیرد به گونه ای که دو سوم آب چشمه به سمت آبشار و از آنجا به دشت های نیاسر و یک سوم آن به محله نو می رود.<br>سنگی که آبراهه آبشار نیاس در آن ایجاد شده و سنگ هایی که بدنه مزبور را تشکیل می دهند از نوع سنگ های آهکی و رسوبی می باشد. به دلیل پدیده های انحلال سنگ آهک توسط جریان های زیرزمینی و گذشت سالیان دراز بافت بسیار زیبا و چشم نوازی مانند استالاکتیت ها و استالاکمیت ها در بدنه آبشار نیاسر به وجود آمده است که موجب جذب گردشگران برای دیدن این زیبایی است. آن گونه که از بقایای خزه های قدیمی و آبراهه های موجود و همچنین گفته های اهالی محل مشخص می شود مسیر آب در قدیم عوض شده است. آبی که از آبشار پایین می ریزد در فصل پاییز کمترین و در فصل زمستان بیشترین مقدار را داراست.', 4, 1),
(26, 'ابیانه', 'اَبیانه روستایی از توابع بخش مرکزی شهرستان نطنز در استان اصفهان است. این روستا در ۴۰ کیلومتری شمال غربی نطنز، در دامنه کوه کرکس است. این روستا یکی از بلندترین نقاط مسکونی در ایران است. ارتفاع از سطح دریا در این روستا ۲۲۲۲ متر می‌باشد.<br>به اعتبار معماری بومی و بناهای تاریخی پُرتنوع آن، از روستاهای مشهور ایران است. ابیانه با آب و هوای معتدل، دارای موقعیت طبیعی مساعدی است.<br>این روستا در تاریخ ۳۰ مرداد ۱۳۵۴ با شماره ثبت ۱۰۸۹ به‌عنوان یکی از آثار ملی ایران به ثبت رسیده‌است.', 4, 1),
(27, 'هشت بهشت', 'کاخ هشت‌بهشت یک کاخ تاریخی در شهر اصفهان است که در دوران صفویان و در سال ۱۰۸۰ قمری ساخته شده است. ساختمان این کاخ در دو طبقه در میان باغی بزرگ بنا شده است و سبک معماری این بنا به شیوه اصفهانی است.<br>کاخ هشت‌بهشت (که روزگاری زیباترین کاخ عالم هم نامیده می‌شد) در سال ۱۰۸۰ هجری و به روزگار شاه سلیمان صفوی در نزدیکی باغ بلبل ساخته شد.<br>باغ وسیعی که عمارت در آن واقع شده، جزئی از باغ بزرگ نقش جهان بوده‌است که شاه اسماعیل اول احداث کرد و در زمان جانشینان او، به خصوص شاه عباس اول، به قطعات متعددی تقسیم شد. باغ هشت بهشت در زمان شاه صفی وجود داشته است و در دوره دو شاه بعدی یعنی شاه عباس دوم و شاه سلیمان بر درختان این باغ زیبا افزودند. لازم است ذکر شود کوشک باغ متعلق به زمان شاه سلیمان صفوی می‌باشد که در آن دوره باغ تکمیل و اصلاح گردید و در سال ۱۰۸۰ هـ. ق که با سومین سال سلطنت شاه سلیمان صفوی مقارن بود، به اتمام رسید و به گفته برخی تاریخدانان این باغ نشیمن هشت سوگلی حرم پادشاه بوده است، که ۴ نفر در طبقة همکف و ۴ نفر دیگر در طبقه اول سکنی گزیده بودند.<br>این بنای دو طبقه با طاقهای زیبا و تزئینات فراوان یکی از نمونه‌های درخشان معماری عصر صفویه به شمار می‌رود. این قصر از همان ابتدای احداث «کاخ هشت بهشت» «هشت به هشت» و «هشت در بهشت» نامیده می‌شده‌است.', 4, 1),
(28, 'مسجد شیخ لطف الله', 'مسجد شیخ لطف‌الله (ساخته شده در ۱۶۰۲ - ۱۶۱۹ میلادی) یکی از مسجدهای تاریخی و شناخته شده شهر اصفهان است و مجموعه تاریخی میدان نقش جهان است که در دوران صفوی ساخته شده است.<br>این مسجد شاهکاری از معماری و کاشی‌کاری قرن یازدهم هجری است که توسط استاد محمدرضا اصفهانی از معماران نامدار آن دوره ساخته شده‌است. مسجد شیخ لطف‌الله به فرمان شاه عباس اول در مدت هجده سال بنا شده و در ضلع شرقی میدان نقش جهان و مقابل عمارت عالی‌قاپو و در همسایگی مسجد شاه واقع شده است.این مکان مذهبی برای تجلیل شیخ لطف‌الله میسی بنا گردیده و سالانه گردشگران زیادی را جذب خود می‌کند.<br>در میانه سال‌های دهه ۱۹۲۰، رضاشاه دستور به بازسازی و مرمت کلی مسجد شیخ لطف‌الله را داد.', 4, 1),
(29, 'پل خواجو', 'پل خواجو، در شهر اصفهان و بر روی رودخانه زاینده‌رود در شرق سی وسه پل قرار دارد.<br>این پل را همچنین پل شاه، پل بابا رکن‌الدین (مسیر رفتن به خانقاه و آرامگاه بابا رکن‌الدین و تخت فولاد) و پل حسن‌بیگ (نام پل قبلی که در زمان شاه‌عباس تخریب شد و این پل به جای آن ساخته شد) هم نامیده‌اند و از بناهای شاه عباس دوم صفوی است که در سال ۱۰۶۰ هجری قمری بنا شده‌است. در میانه پل خواجو، ساختمانی مخصوص، که به بیگلربیگی شهرت دارد بنا شده‌است که برای اقامت موقتی شاه صفوی و خانواده او ایجاد شده بود و هم اکنون نیز وجود دارد و طاق‌های آن دارای تزئینات نقاشی بسیار زیبایی است و در واقع پل خواجو به خاطر معماری و تزئینات کاشی کاری آن از دیگر پل های زاینده رود مشهورتر است.<br>درازای این پل ۱۳۳ متر و پهنای آن ۱۲ متر است. این پل را به نام های دیگری نیز خوانده‌اند ولی به سبب قرار گرفتن در محله خواجو به پل خواجو معروف است.<br>پل خواجو به خاطر معماری و تزئینات کاشی‌کاری آن از دیگر پلهای زاینده رود مشهورتر است. این پل که در دوران صفوی یکی از زیباترین پل های جهان به شمار می‌رفت، بیشتر به منزله سد و بند بوده‌است. در میان هریک از دو ضلع شرقی و غربی پل ساختمانی بنا شده که شامل چند اتاق مزین به نقاشی است. این ساختمان که «شاه نشین» نامیده می‌شود، در آن دوره جایگاه بزرگان و امیرانی بوده که برای تماشای مسابقات شنا و قایقرانی بر روی دریاچه مصنوعی به این مکان فراخوانده می‌شدند.', 4, 1),
(30, 'جاده چالوس', 'جاده چالوس (با نام رسمی جاده ۵۹)، یکی از مهم ترین جاده‌ها در ایران است که از میان شهر کرج در استان البرز شروع و به شهر چالوس در مازندران وصل می‌شود و یکی از زیباترین جاده‌های جهان می باشد.<br>محور چالوس - کرج از بی نظیرترین مناظر و چشم‌اندازهای بکر و طبیعی و از پرطرفدارترین مسیرهای منتهی به شمال کشور می‌باشد که همین امر گاهی سبب ترافیک‌های سنگین در تعطیلات می‌شود.<br>تا اواخر دوره قاجار، جاده‌ای بین کرج - چالوس وجود نداشته و تنها یک مسیر خاکی و مالرو، راه دسترسی به روستاهای این منطقه را تشکیل می‌داده اما به گونه‌ای نبود که ارتباط دهنده شمال و جنوب کشور باشد. این جاده در دوران پادشاهی رضاشاه و با دستور وی در سال ۱۳۱۲ مورد بهره‌برداری قرار گرفت.<br>از این زمان به بعد، انواع رستوران‌ها و واحدهای اقامتی و پذیرایی در کنار این جاده شکل گرفت و با ساخت سد امیر کبیر (سد کرج) در سال ۱۳۴۰ به جاذبه‌ها و زیبایی‌های این جاده افزوده شد و در زمانی اندک، این جاده به یکی از تفریحگاه‌های مهم گردشگری ایران تبدیل شد.', 5, 1),
(31, 'پیست اسکی دیزین', 'پیست اسکی دیزین، مهم‌ترین پیست اسکی ایران و منطقه خاورمیانه است که در شهر کرج واقع شده‌است.<br>پیست اسکی دیزین اولین پیست اسکی در ایران است که از طرف فدراسیون جهانی اسکی مورد تأیید برای برگزاری مسابقات رسمی شناخته شد و عنوان بین‌المللی به خود گرفت.<br>', 5, 1),
(32, 'کاروانسرای شاه عباسی کرج', 'کاروانسرای شاه‌عباسی کرج بنایی سنگی - آجری مربوط به دوران صفویه است که در شهر کرج در استان البرز و در سمت جنوب شرقی میدان توحید جای گرفته‌است. این بنای تاریخی در ۲۲ فروردین ماه سال ۱۳۵۶ با شماره ۱۳۶۸ در فهرست آثار ملی ایران ثبت شد.<br>کاروانسرای بین سال‌های ۱۰۷۸ تا ۱۱۰۹ و در زمان شاه سلیمان صفوی ساخته شده‌است. این بنا دارای بیش از ۳۰۰۰ متر مربع مساحت است و می توان آن را یکی از ارزشمندترین آثار دوره صفویه دانست که در شاهراه ابریشم قرار داشته است.<br>این کاروانسرا در ابتدای ساخت برای کاروان‌ها و مرکب‌هایشان به عنوان مکان استراحت بوده و در اوایل دوران قاجاریه تبدیل به پادگان یا قلعه نظامی شد و در اواخر دوران قاجار نیز به عنوان مدرسه (موسوم به فلاحت) از آن استفاده می‌شده‌است.', 5, 1),
(33, 'سد امیرکبیر (سد کرج)', 'سد امیرکبیر (سد کرج) در شمال شهر کرج و در ۲۵ کیلومتری جادهٔ کرج به چالوس قرار دارد. این سد، اولین سد چندمنظوره کشور و یکی از منابع تأمین آب شهر تهران می‌باشد.<br>دریاچه این سد، یکی از مراکز طبیعی مهم پرورش ماهی‌های قزل‌آلای رنگین‌کمان، قزل‌آلای خال‌قرمز، ماهی سفید و ماهی سیاه است. انباشته شدن آب رودخانه کرج در پشت سد امیرکبیر علاوه بر کارایی آن در تولید انرژی برقابی و تامین بخشی از آب آشامیدنی شهر تهران، باعث بوجود آمدن دریاچه ای زیبا گردیده است که موجب استفاده‌های گردشگری و تفریحی و ورزشی از سد کرج شده است و یکی از بهترین استراحتگاه ها و جاذبه های طبیعی کرج را به وجود آورده است.<br>سد امیرکبیر یک سد چند منظوره است که می‌تواند آب لوله کشی شهرتهران و گسترش کشاورزی و آبرسانی به ۵۰ هزار هکتار زمین‌های کشاورزی کرج را تامین کند. نیروگاه برق آبی این سد نیز با بیش از چهل شش سال فعالیت به شبکه برق کشور متصل و توانایی تولید ۹۰ مگاوات برق را داراست. ', 5, 1);

