INSERT INTO `Heroes` (`n`, `name`, `description`, `fstate`, `active`) VALUES
(1, 'سیدعلی آقا قاضی تبریزی', 'حاج سید علی آقا قاضی فرزند حاج سید حسین قاضی، پس از تحصیل علوم ادبی و دینی در تبریز، در سن 28 سالگی به نجف اشرف مشرف شد و تا آخر عمر آن جا را موطن اصلی خویش قرار داد. او پس از اقامت در نجف اشرف، تحصیلات حوزوی خود را نزد اساتیدی از جمله فاضل شربیانی، شیخ محمد مامقانی، شیخ فتح الله شریعت، آخوند خراسانی و... ادامه داد و سرانجام کوشش های خستگی ناپذیرش در راه کسب علم، کمال و دانش، در سن 27 سالگی به ثمر نشست و این جوان بلند همت در عنفوان جوانی به درجه اجتهاد رسید. میرزا علی آقا قاضی پس از سال‌ها تدریس معارف اسلامی، در روز دوشنبه چهارم ماه ربیع المولود سال 1366 قمری مطابق هفتم بهمن ماه در نجف اشرف وفات کرد و در وادی السلام نزد پدر خود دفن شد.', 1, 1),
(2, 'علامه عبدالحسین امینی', 'شیخ عبدالحسین امینی در اوان جوانی و زمانی که هنوز چند دهه باقی بود تا کتاب "الغدیر" را به رشته تحریر درآورد، از علمای بزرگ و مراجع رده اول اجازه اجتهاد و اجازه روایت دریافت داشت. او در پاکی عمل و اخلاق اسلامی و صلاح نفس نیز عالمی بود برجسته و ربانی که کمتر کسی بدین مرحله رسیده است. علامه بزرگ سفرهای بسیاری انجام داد و در این سفرها حقایق شیعه را باز می گفت و در پی ایجاد یک اتحاد واقعی و راستین بین مسلمانان بود و چیزی که او را در این کار بسیار یاری می رساند خطابت و سخنرانی بی نظیر او بود. یکی از عمده‌ترین دلائل تألیف کتاب "الغدیر" ارادت علامه به علی بن ابیطالب(ع) بود او شیفته دریای بیکران ولایت بود و چنان در این دریای بیکران غوطه ور شد که خیال و اهداف دیگری برایش باقی نماند.', 1, 1),
(3, 'شهید مهدی باکری', 'مهدی باکری در سال ۱۳۳۳ در میاندوآب و در خانواده‌ای مذهبی به دنیا آمد. در همان آغاز کودکی مادرش را از دست داد. او و دوستانش نقش مهمی در برپایی تظاهرات شهر تبریز در ۱۵ خرداد ۱۳۵۴ و ۱۳۵۵ داشتند. همان زمان وی توسط ساواک شناسایی شد و بارها برای بازجویی و تحت نظر آزاد شد. پس از اخذ دیپلم وارددانشگاه تبریز شد و در رشته مهندسی مکانیک شروع به تحصیل کرد. در حین تحصیل خبر مرگ برادرش، علی باکری را به وی دادند. بدین ترتیب او دومین عضو خانواده خویش را نیز از دست داد.', 2, 1),
(4, 'شهید حمید باکری', 'از فرماندهان سپاه پاسداران انقلاب اسلامی در جنگ ایران وعراق و برادرعلی باکری ومهدی باکری بود. وی درعملیات خیبر در اثر اصابت آرپی جی کشته شد. پیکر او در میدان جنگ باقی ماند و به کشور بازگردانده نشد.', 2, 1),
(5, 'پوریا ولی', 'راد مردی که بعد از عمری بر خاک نشاندن پهلوانان و قهرمان در کشورهای مختلف سرانجام با بر زمین زدن نفس خویش به عرفان رسید و جاودانه شد. طبق محاسبه مورخان روس، در ۲۳ ژوئیه سال ۱۳۲۲ میلادی مطابق با ۷۲۲ هجری قمری اول مرداد ماه روی در نقاب خاک کشید و به سرای باقی شتافت. با توجه به اینکه این پهلوان نامی و عارف بزرگ و شاعر مردمی نسبت از خوی می برد و در همین خاک به لقای الهی رسید. باید مراسمی درخور شخصیتش برگزار کرد. به همین سبب از هم اکنون تاریخ فوت وی را یادآور می شویم تا دست اندرکاران بخصوص متولیان فرهنگی و ورزشی شهرمان از هم اینک به فکر برگزاری مراسم پرشکوه باشند.', 2, 1),
(6, 'رحیم مؤذن زاده', 'مرحوم رحیم مؤذن زاده اردبیلی در سال 1303 در اردبیل به دنیا آمد . پدرش شیخ عبدالکریم بسیار خوش صدا ، خوش خلق و آشنا به ردیفهای موسیقی بود . شیخ عبدالکریم در سال 1326 از رادیو اذان گفت و بعد از آن ، پسرش رحیم نیز با او همراه شد . اذان ماندگان رحیم مؤذن زاده در دستگاه بیات ترک گوشه روح الارواح اجرا شده است .', 3, 1),
(8, 'قیصر امین پور', 'قیصر امین‌پور در ۲ اردیبهشت ۱۳۳۸ در روستای گتوند (شهرستان امروزی) از توابع شهرستان شوشتر در استان خوزستان به دنیا آمد. دوره راهنمایی و متوسطه خود را در مدرسه راهنمایی دکتر معین و آیت اله طالقانی دزفول گذراند و در سال ۱۳۵۷ در رشته دامپزشکی دانشگاه تهران پذیرفته شد، ولی پس از مدتی از تحصیل این رشته انصراف داد<br><br>قیصر امین‌پور، در سال ۱۳۶۳ بار دیگر اما در رشته زبان و ادبیات فارسی به دانشگاه رفت و این رشته را تا مقطع دکترا گذراند و در سال ۱۳۷۶ از پایان‌نامه دکترای خود با راهنمایی دکتر محمدرضا شفیعی کدکنی با عنوان «سنت و نوآوری در شعر معاصر» دفاع کرد. این پایان‌نامه در سال ۱۳۸۳ و از سوی انتشارات علمی و فرهنگی منتشر شد.', 13, 1),
(9, 'حسن روحانی', 'در ۲۲ فروردین ۱۳۹۲، حسن روحانی با نام «دولت تدبیر و امید»، شعار اعتدال‌گرایی ، نماد کلید و رنگ بنفش برای انتخابات ریاست جمهوری رسماً اعلام نامزدی کرد. وی در ۱۷ اردیبهشت، با حضور در ستاد انتخابات وزارت کشور، نام خود را برای رقابت ۲۴ خرداد ثبت کرد.سرانجام وی با اعلام وزارت کشور با کسب ۱۸٬۶۱۳٬۳۲۹ رأی و کسب ۵۰/۷۰ درصد آرا رسماً به عنوان هفتمین رئیس جمهوری اسلامی ایران برگزیده شد.روحانی برای انتخاب مجدد با شعار دوباره ایران در انتخابات ریاست‌جمهوری ۲۹ اردیبهشت ۱۳۹۶ رقابت کرد. درپی انتخاباتی که در روز جمعه ۲۹ اردیبهشت در ایران و با شرکت ایرانیان سراسر جهان برگزار شد، حسن روحانی با ۲۳٬۶۳۶٬۶۵۲ رای و کسب ۵۷ درصد آرا رئیس جمهور ایران باقی ماند.<br><br>حسن روحانی اولین رئیس جمهور ایران است که بعد از انقلاب ایران، با رئیس جمهور ایالات متحده آمریکا، ارتباط برقرار نمود. این ارتباط با باراک اوباما و از طریق مکالمه تلفنی صورت گرفت.همچنین او اولین رئیس جمهور ایران است که بعد از انقلاب اسلامی با نخست وزیر انگلیس، دیوید کامرون، به عنوان عالی‌ترین مقام اجرایی انگلستان دیدار کرد.', 15, 1),
(10, 'آیت الله هاشمی رفسنجانی', 'اکبر هاشمی رفسنجانی یکی از چهره‌های تأثیر گذار در روند انقلاب ایران و همچنین دوران پس از آن تا امروز است.آشنایی و دوستی او با روح‌الله خمینی از اواخر دهه سی شمسی آغاز شد؛ ولی فعالیت سیاسی خود را از سال ۱۳۴۰ آغاز کرد. او در دوران قبل از انقلاب، بسیار به خمینی نزدیک بود.<br><br>هاشمی پیش از انقلاب، یکی از مخالفان برنامه‌های نوگرایانه شاه (انقلاب سفید) شناخته می‌شد. با تبعید روح‌الله خمینی، رفسنجانی در ایران ماند و مخالفت‌هایش با این برنامه‌ها افزایش یافت. این مخالفت‌ها سرانجام به دستگیری و زندانی شدن او منجر شد. در مجموع و از سال ۱۳۳۷ تا سال ۱۳۵۷ هاشمی رفسنجانی ۷ بار و مجموعاً ۴ سال و ۵ ماه به جرم فعالیت مخفیانه علیه حکومت پهلوی به زندان افتاد.', 21, 1),
(11, 'حافظ', 'خواجه شمس‌الدین محمد بن بهاءالدّین محمد حافظ شیرازی، معروف به حافظ (ملقب به لسان الغیب، لسان العرفا، ناظم الاولیا) شاعر بزرگ سدهٔ هشتم ایران (برابر قرن چهاردهم میلادی) و یکی از سخنوران نامی جهان است. بیش‌تر شعرهای او غزل هستند که به غزلیات حافظ شهرت دارند. گرایش حافظ به شیوهٔ سخن‌پردازی خواجوی کرمانی و شباهت شیوهٔ سخنش با او مشهور است. <br>او از مهم‌ترین تأثیرگذاران بر شاعران پس از خود شناخته می‌شود، تا جایی که برخی از شعرا و ادبای خارجی مانند گوته، از حافظ الهام گرفته اند.<br>مهم ترین اثر حافظ یعنی دیوان غزلیات حافظ، کتابی بسیار مهم برای تمامی ایرانیان است که در تمامی جشن ها آن را می خوانند و با آن فال می گیرند.<br>آرامگاه حافظ (حافظیه) هم اکنون در شهر شیراز قرار دارد و یکی از مهم ترین مکان های گردشگری این شهر می باشد.', 17, 1),
(12, 'سعدی', 'ابومحمّد مشرف‌الدین مصلح بن عبدالله بن مشرف یا همان سعدی، ملقب به افصح المتکلمین و شیخ اجل، شاعر و نویسنده پارسی گوی نامدار ایرانی است که در قرن هفتم می زیست.<br>او در نظامیه بغداد (که مهمترین مرکز علم و دانش جهان اسلام در آن زمان به حساب می‌آمد) تحصیل نمود و پس از آن به‌عنوان خطیب به مناطق مختلفی از جمله شام و حجاز سفر کرد. وی سپس به زادگاه خود، شیراز، برگشت و تا پایان عمر در آنجا اقامت گزید.<br>از آثار سعدی می توان به گلستان، بوستان و کلیات سعدی (مجموعه ای از تمام آثار او) اشاره کرد.<br>آرامگاه سعدی در سعدیه، واقع در شهر شیراز است که از مهم ترین مکان های گردشگری این شهر است.<br>به‌منظور تجلیل از سعدی، روز اول اردیبهشت ماه (روز آغاز نگارش کتاب گلستان) در ایران به‌عنوان روز سعدی نامگذاری شده‌است.<br>', 17, 1),
(13, 'محمّد کریم خان زند', 'محمّد کریم‌خان زند، فرمانروای ایران( از سال ۱۱۶۳ تا ۱۱۹۳ هجری قمری) و بنیان‌گذار دودمان زندیه بود که در شهر شیراز بناهای باشکوه و زیبایی ساخت، از جمله بازار وکیل، حمّام وکیل، مسجد وکیل، ارگ کریم خانی و ...<br>کریم خان به خاطر عشق و علاقه ای که به مردم و نوع دوستی داشت، از لقب وکیل الرعایا به جای لقب شاه برای خود استفاده می کرد.', 17, 1),
(14, 'علامّه محمدتقی جعفری', 'استاد علامه محمدتقی جعفری، فقیه و فیلسوف مشهور، پس از تحصیلات عالیه علوم اسلامی در شهر تبریز، تهران، قم و نجف اشرف در محضر آیات عظام، میرزا فتاح شهیدی، شیخ محمدرضا تنكابنی، شیخ كاظم شیرازی، سید عبدالهادی شیرازی، سید ابوالقاسم خویی و سید محسن حكیم كسب فیض نمود. تا حال حاضر بیش از هشتاد اثر از آثار استاد به طبع رسیده است که از آثار برجسته ایشان شرح و تفسیر بر نهج‌البلاغه می باشد كه 27 جلد آن به زبان فارسی چاپ و منتشر شده است. چاپ بعضی از آثار ایشان همچون شرح مثنوی در 15 جلد تجدید گردیده و تحت نظارت ایشان و طی 12 سال كامل‌ترین كشف الابیات مثنوی مولوی در 4 جلد منتشر شده است.', 1, 1),
(16, 'پروفسور رحیم رحمان زاده', 'پروفسور رحیم رحمان‌زاده پایه گذار جراحی مدرن ارتوپدی و تروماتیک ارتوپدی در سطح بین‌المللی و رئیس مرکز بین‌المللی جراحی مفاصل و استخوان برلین، اصالتاً آذربایجانی و متولد شهرستان شبستر است. وی در ۱۸ سالگی به ترکیه رفت و تحصیلات پزشکی عمومی خود را در استانبول ترکیه گذراند. پس از آن برای تکمیل تحصیلات تخصصی خود در رشته جراحی عمومی و جراحی تصادفات ارتوپدی راهی آلمان شد. رحمان زاده در دانشگاه ماینز به مقام پروفسوری نایل آمد و از سال ۱۹۷۳ ریاست کرسی جراحی دانشگاه برلین را برعهده گرفت. او به عنوان پایه گذار جراحی مدرن ارتوپدی ، تکنیک‌های جراحی خاص در این رشته را ابداع کرد. پروفسور رحمان زاده درحال حاضر به عنوان پرزیدنت سمینارهای اروپایی و جهانی مقام پرزیدنتی جمعیت جراحان ارتوپدی تصادفات و سوانح ، جمعیت جراحان پلاستیک آلمان و جمعیت جراحان عمومی این کشور را نیز برعهده دارد. این همه در حالی است که تاکنون هیچ تبعه خارجی موفق نشده است به این مدارج علمی در کشور آلمان دست یابد.', 1, 1),
(17, 'شیخ محمود شبستری', 'شیخ سعدالدین محمود بن امین الدین عبدالکریم بن یحیی شبستری، از عرفا و شعرای نامی قرن هفتم و هشتم هجری است. او در قرن 7 و 8 هجری و در عصر حکومت ایخلخانان می‌زیسته است. شبستری در سال ۷۲۰ هجری در ۳۳ سالگی وفات یافته و در شبستر مدفون شد.', 1, 1),
(18, 'استاد محمد حسین بهجت تبریزی', 'سید محمد حسین بهجت تبریزی (ملقب به شهریار) از بهترین شاعران معاصر است که به زبان فارسی و ترکی سروده است.<br>وی در روستای خشکناب در بخش قره چمن آذربایجان متولد شد. پدرش، حاجی میر آقا خشکنابی، از وکلا مبرز و مردی فاضل و خوش محاوره و از خوش نویسان دوره خود و کریم الطبع بود.<br>او در اوایل شاعری بهجت تخلص میکرد و بعداْ دوباره با فال حافظ تخلص خواست که دو بیت شاهد از دیوان آمد و خواجه تخلص او را شهریار تعیین کرد.<br>او تحصیلات خود را در مدرسه متحده و فیوضات و متوسطه تبریز و دارالفنون تهران گذراند و تا کلاس آخر مدرسه طب تحصیل کرد و به مدارج بالایی دست یافت.<br>استاد شهریار پس از هشتاد و سه سال زندگی شاعرانه پربار و افتخار در ۲۷ شهریور ماه ۱۳۶۷ به ملکوت اعلی پیوست و پیکرش در مقبره الشعرای تبریز (که مدفن بسیاری از شعرا و هنرمندان آن دیار است) به خاک سپرده شد.', 1, 1),
(19, 'پروین اعتصامی', 'پروین اعتصامی، از شاعران گرانمایه ایران، و یکی از بهترین شاعران زن در ایران، در سال 1285 خورشیدی در تبریز تولد یافت.<br>وی ادبیات فارسی و عربی را با مهارت هر چه تمام در نزد پدر آموخت. استعداد و قریحه لبریز او از هفت سالگی بروز کرد. این شاعر معاصر با آن که از وی دعوت شد با طبعی بلند ورود به دربار را نپذیرفت و مدال وزارت معارف ایران را رد کرد.<br>اشعار وی بیشتر در مورد مسایل اجتماعی و ظلم و بیداد شاهان و فقر عمومی می باشد.<br>پروین در سال 1320 و در سن 35 سالگی، رخ در نقاب خاک کشید.', 1, 1),
(21, 'جبّار باغچه بان', 'جبار باغچه‌بان، زاده 1264 در ایروان، مبدع روش آموزش ناشنوايان در ايران و پايه‌گذار آموزش و پرورش پيش از دبستان و از پيشگامان فرهنگ و ادبيات کودكان است.<br>پدر و جد وی از اهالی تبريز بودند. باغچه‌بان تعليم و تربيت زنان و كودكان را مهم می شمرد و به رغم مخاطرات موجود، پنهاني به تدريس دختران می پرداخت.<br>از اولين آثار او برای كودكان، داستان های منظوم «قيزيللی ياپراق» (برگ زراندود) و «بايرامچيليق» (مژده‌رساني عيد) است. همچنين در روش‌های تدريس خواندن و نوشتن و آموزش ناشنوايان، سيزده اثر از وي منتشر شده‌است. آثار او به زبان تركی بالغ بر دوازده كتاب است كه از آن ميان، ترجمه رباعيات خيام (به نام رباعيات آذری خيام)، ارزش خاصی دارد. رباعيات باغچه‌بان، كه در 1337 به چاپ رسيد، آيينه افكار و فلسفه زندگی اوست.<br>باغچه بان، سرانجام در سال 1345، چشم از جهان فرو بست.', 1, 1),
(22, 'علی دایی', 'علی دایی متولد اول فروردین سال 1348 در شهر اردبیل بازیکن ایرانی و از مشهورترین بازیکنان تاریخ تیم ملی فوتبال ایران است و تا پایان جام جهانی 2006 بازیکن اصلی و کاپیتان تیم ملی ایران بود.<br>دایی 109 گل ملی در کارنامه خود دارد و با فاصله زیادی، بهترین گلزن بازیهای ملی در تاریخ فوتبال جهان است. دایی سابقه بازی در تیمهای آلمانی همچون بایرن مونیخ را دارد و به همراه این تیم به نایب قهرمانی لیگ قهرمانان اروپا (که بزرگترین رقابت باشگاهی در اروپا و حتی جهان است) دست یافته است.<br>دایی فوتبال را از باشگاه های کوچک شهر اردبیل همچون استقلال اردبیل و شهرداری اردبیل آغاز کرد و دوره خدمت وظیفه را در تیم پاس گذراند. سپس به تیم بانک تجارت پیوست و از آنجا به تیم ملی دعوت شد.<br>وی بعدها به تیم پرسپولیس تهران و السد قطر پیوست و سرانجام راهی اروپا شد و به تیم آرمینیا بیله فیلد آلمان پیوست. مقصد بعدی او بایرن مونیخ آلمان بود. سپس به هرتابرلین پیوست.<br>علی دایی همچنین توانست تیم سایپا را قهرمان ششمین لیگ برتر کشور سازد. علی دایی در زمینه تحصیلی فرد موفقی بوده و لیسانس خود را در رشته متالوژی از دانشگاه صنعتی شریف دریافت نموده است و علاوه بر آن در تجارت نیز موفق بوده است. وی گرداننده چندین شرکت بزرگ و رستورانهای متعدد است. علی دایی همراه با مادلین آلبرایت و دیوید بکام با سازمان یونیسف همکاری داشته اند.', 3, 1),
(23, 'باقرخان', 'باقر خان (زاده سال ۱۲۴۰ در تبریز) مشهور به سالار ملّی، از مبارزان جنبش مشروطه بود.<br>شغل وی بنّائی بود ولی در جریان مشروطیت به مجاهدین پیوست. او ریاست مجاهدین محله خیابان تبریز را به عهده داشت.<br>پس از به توپ بستن مجلس، به دستور انجمن ایالتی همراه با ستارخان به جنگ مسلحانه با قشون دولتی که تبریز را در محاصره داشت، پرداخت. همزمان با او، ستارخان در محله امیرخیز، محله دیگر تبریز، با دولتیان جنگ می‌کرد. در اثر همکاری او با ستّارخان کار مشروطه طلبان پیشرفت کرده و تبریز از محاصره درآمد. پس از پیروزی مجاهدین، مجلس شورای ملی باقرخان را به لقب سالار ملی ملقب ساخت، و از او تقدیر نمود.', 1, 1),
(24, 'ستارخان', 'ستارخان (زاده ۲۸ مهر ۱۲۴۵ در بیشک ورزقان) با نام اصلی ستارخان قره‌داغی، از سرداران آذری و ترک‌زبان جنبش مشروطه ایران و ملقب به سردار ملی است. وی در برابر نیروهای دولتی ضد مشروطه در تبریز ایستاد.<br>ستارخان، سومین پسر حاج حسن قره‌داغی در ۲۸ مهر ۱۲۴۵ خورشیدی در روستای بیشک ورزقان در آذربایجان به دنیا آمد.<br>وی در مقابل قشون عظیم محمدعلی‌شاه پس از به توپ بستن مجلس شورای ملی و تعطیلی آن که برای طرد و دستگیر کردن مشروطه‌خواهان تبریز به آذربایجان گسیل شده بود، ایستادگی کرد و بنای مقاومت گذاشت.<br>ستارخان مردم را بر ضد اردوی دولتی فراخواند و خود رهبری آن را بر عهده گرفت و به همراه سایر مجاهدان و باقرخان، به مدت یک سال در برابر قوای دولتی ایستادگی کرد و نگذاشت تبریز به دست طرفداران محمدعلی‌شاه بیفتد.<br>اختلاف او با شاهان قاجار و اعتراض به ظلم و ستم آنان، به زمان کودکی‌اش برمی‌گشت.', 1, 1),
(25, 'شیخ صفی الدین اردبیلی', 'شیخ صفی‌الدین ابوالفتح اسحاق اردبیلی (۷۱۳ - ۶۳۱ خورشیدی برابر ۷۳۵ - ۶۵۰ قمری) نیای بزرگ دودمان صفویان ایران است و نیز هشتمین نسل از تبار فیروزشاه زرین‌کلاه بود که در منطقه مغان مقیم شده بود.<br>شیخ صفی‌الدین اشعاری به زبان آذری در کتاب صفوةالصفا و سلسلةالنسب سروده است. دودمان صفوی نام خویش را از وی گرفته بودند. او پایه‌گذار خانقاه صفوی در اردبیل بود که با گذشت زمان پیروان بسیاری را به دست آورد.', 3, 1),
(26, 'حسین رضازاده', 'حسین رضازاده (زاده ۱۳۵۷ در اردبیل)، ملی‌پوش سابق تیم ملی وزنه‌برداری ایران، دارندهی دو مدال طلای المپیک است. او از سال ۱۳۸۸ تا ۱۳۹۳ رئیس فدراسیون وزنه‌برداری ایران بود. همچنین، وی در انتخابات چهارمین دوره شورای شهر تهران (۱۳۹۲ تا ۱۳۹۶) به عنوان نماینده انتخاب شد.<br>وی صاحب رکورد دو ضرب دسته +105 کیلوگرم تا پیش از مسابقات جهانی ۲۰۱۵ آمریکا بود و پیش از مسابقات جهانی وزنه‌برداری در فرانسه صاحب رکورد یک‌ضرب دسته +۱۰۵ کیلوگرم این رشته ورزشی نیز بود.<br>او در ایران به عنوان «قهرمان قهرمانان» نیز انتخاب شده‌است. آخرین قهرمانی وی مربوط به بازی‌های آسیایی دوحه است. او در مجموع ۸ مدال طلا (۲ مدال المپیک؛ ۴ مدال جهانی و ۲ مدال بازی‌های آسیایی) و ۲ مدال برنز گرفته است.', 3, 1),
(27, 'تاج اصفهانی', 'وی در سال ۱۲۸۲ در اصفهان متولد شد. پدرش شیخ اسماعیل معروف به تاج‌الواعظین بود که تا حدی با دستگاه‌های موسیقی ایران آشنایی داشت. تاج در ده سالگی نزد پدر و استادانی چون سید عبدالرحیم اصفهانی، نایب اسدالله، میرزا حسین ساعت ساز (خضوعی)، میرزا حسین عندلیب و حبیب شاطرحاجی به دانش اندوزی پرداخت. وی به اشعار سعدی علاقه زیادی داشت و گزیده‌هایی از اشعار سعدی و دیگر شاعران را حفظ بود و در هنگام اجرای آواز به مناسبت زمان و محیط از آن اشعار استفاده می‌کرد. او از همان سال ۱۳۱۹ همکاری با رادیو را آغاز کرد و از ۱۳۲۸ به همکاری با رادیو اصفهان پرداخت. تاج ضمن خوانندگی، سرپرست نوازنندگان رادیو اصفهان گردید. وی در رادیو اصفهان به اجرای برنامه‌هایی با تار اکبرخان نوروزی و برنامه آموزش گوشه‌های دستگاه‌های موسیقی ایرانی پرداخت.<br>از جمله شاگردان او محمدرضا شجریان، اصغر شاهزیدی، علیرضا افتخاری، مرتضی شریف، ناصر یزدخواستی، رضا قرنیان اصفهانی و حمیدرضا نوربخش را می‌توان نام برد.<br>وی در ۱۳ آذر ماه ۱۳۶۰ بدرود حیات گفت. آرامگاه او در تخت فولاد قرار دارد.', 4, 1),
(28, 'استاد محمود فرشچیان', 'محمود فرشچیان در سال 1308 شمسی در اصفهان، دیده به جهان گشود. پدرش، حاج غلامرضا فرشچیان، از تجار فرش بود و در کار هنر قالی بافی دست داشت.<br>این زمینه مساعد برای پرورش ذوق و شوق او در راه آشنایی با هنر موثر افتاد. از سال های پیش از مدرسه از روی نقشه های قالی طرح می زد، تا به تدریج دستش در کار نقش پردازی روان گردید. فرشچیان در طی تحصیلات مقدماتی در محضر استاد میرزاآقا امامی اصفهانی هنرمند چیره دست و پرآوازه دیار اصفهان، به آموختن نقاشی دلبستگی تمام پیدا کرد و دل در گرو نقش های زیبا بست. او از کار هنر احساس رضایت و شادمانی داشت. از کلاس هفتم دبیرستان فرشچیان قدم به هنرستان هنرهای زیبای اصفهان گذاشت و چهار سال در آن جایگاه عاشقی، زیر نظر استاد عیسی بهادری، استاد نابغه و توانمند نقاشی قالی، مینیاتور، نقاشی رنگ و روغن، به فراگیری اصول و مبانی طراحی نقوش سنتی (نقشه قالی، تذهیب، مینیاتور) پرداخت.<br>بدون شک نقش استاد عیسی بهادری در پرورش و خلاقیت های محمود فرشچیان نقشی فوق العاده ارزشمند بود. استاد در بیان احساس و تجسم شعرگونه عواطف، به شیوه ای کاملا جدید موفق به هماهنگی و همگامی میان مضمون و محتوا و شکل و فرم نقاشی هایش گردید و این مشخصه اصلی کار او در نقاشی شد. فرشچیان در جوانی بسیار پرتلاش و خستگی ناپذیر به کار و طراحی نقوش مختلف میپرداخت. او در مطالعه آثار تاریخی شهر اصفهان (چهل ستون، عالی قاپو، مسجد شیخ لطف الله و ...) طرحهای اسلیمی و ختایی، کاشی کاری های بی نظیر آن آثار سر از پا نمیشناخت و چون محققی، موشکافانه این نقش ها را مطالعه می کرد. فرشچیان حتی در دوره سربازی نیز دست از قلم و رنگ برنداشت و آثاری دیدنی آفرید که مورد تشویق مقامات قرار گرفت. <br>سال 1329 بعد از 6 سال فعالیت و کسب هنر در هنرستان هنرهای زیبای اصفهان، فرشچیان به کسب دیپلم عالی نائل آمد. آثار سالهای شاگردی استاد هنوز در مرکز هنرستان، موجود است . بدون تردید استاد محمود فرشچیان یکی از پدیده های هنر نگارگری (مینیاتور) در عصر حاضر است و او در طراحی و شناخت و بکارگیری رنگها فوق العاده قدرتمند و قوی است. این قدرت قلم و صلابت و استواری خطوط قلم گیری است که از او هنرمندی پرآوازه و مشهور ساخته است. از نظر محتوا و مضمون، آثار استاد عمدتا با الهام از ادبیات عرفانی و باورهای مذهبی ساخته و پرداخته شده اند. سخن آخر اینکه امروز استاد فرشچیان از پس نزدیک به پنجاه سال کسب هنر و مهارت در کار نقاشی و خلق نقشهای دلنشین و جشم نواز، هنرمندی است که به جرآت میتوان او را صاحب سبک جدید در نقاشی ایرانی (مینیاتور) دانست.', 4, 1),
(29, 'هاتف اصفهانی', 'سید احمد حسینی متخلص به هاتف اصفهانی از شعرای نامی ایران در عهد افشاریه و زندیه است.<br>اصل خاندان او از اهل ارودباد آذربایجان بوده که در زمان پادشاهان صفوی از آن دیار به اصفهان هجرت کرده و در این شهر متوطن گردیده‌اند. تولد هاتف در نیمهٔ اول قرن دوازدهم اتفاق افتاده و در آن شهر به تحصیل ریاضی و حکمت و طب پرداخته و گویا در این فنون از محضر میرزا محمد نصیر اصفهانی استفاده کرد و در شعر نیز مشتاق را راهنما و استاد خود اختیار نموده است.<br>هاتف در جوانی به سرودن اشعار خود پرداخت و در طول زندگی آرام خود از مدح شاهان و روی آوردن به دربار سلاطین خود داری کرد و بیشتر به مطالعه و حکمت و عرفان‌مشغول بود. وی در سال ۱۱۹۸ درگذشت.<br>هاتف اصفهانی شاعری توانا و مسلط به زبان و ادب فارسی بود. وی از سبک شعرای متقدم ایران به ویژه حافظ و سعدی پیروی می‌کرد و طبع خود را در سرایش تمامی قالب های شعری اعم از غزل، قصیده و رباعی، ترجیع‌بند و ترکیب‌بند آزمود. شهرت عمده هاتف به سبب شاهکار بزرگ ادبی او (ترجیع‌بند عرفانی) است که در آن هم از حیث حسن ترکیب الفاظ و هم از حیث توصیف معانی داد سخن داده است. وی مرثیه‌های زیبایی نیز با استفاده از ماده تاریخ (حروف ابجد) در مرگ بزرگان و دوستان خود سرود که این اشعار از ارزش بالایی برخوردارند.', 4, 1),
(30, 'حسن کسائی', 'حسن کسائی (۳ مهر ۱۳۰۷، اصفهان - ۲۵ خرداد ۱۳۹۱، اصفهان) از استادان برجسته موسیقی ایرانی و نوازنده سرشناس نِی و سه تار بود.<br>او در خانواده‌ای تاجرپیشه به دنیا آمد. پدرش، «حاج سید جواد کسائی» از تاجران به نام آن زمان اصفهان بود که دلیل علاقه و انسی که با موسیقی داشت، با اساتید و بزرگان آن زمان مانند سید حسین طاهرزاده، جلال‌الدین تاج اصفهانی، اکبر خان نوروزی، خاندان شهناز (شعبان‌خان، حسین آقا، علی آقا و جلیل شهناز)، غلامحسین سارنج و ادیب خوانساری رفت‌وآمد می‌نمود. به طوری که منزل آقا سید جواد، محفلی بود برای تجدید دیدار و نیز ساز و آواز اساتید به نام موسیقی اصفهان. این آمد و شدها موجب شد حسن کسائی از کودکی با موسیقی آشنا شود و به مرور زمان، علاقه زیادی خصوصاً به ساز نِی پیدا کرد.<br>وی مدتی آواز و گوشه‌های موسیقی ایران را نزد استاد تاج اصفهانی و ادیب خوانساری آموخته و نِی را از مهدی نوایی فرا گرفت. وی هر زمان که به تهران می‌رفت از محضر استاد ابوالحسن صبا استفاده می‌نمود.', 4, 1),
(31, 'سهراب سپهری', 'شاعر و نقاش نامدار ایرانی در 15 مهر ماه 1307 در شهر کاشان به دنیا آمد. پدرش اسدالله<br>سپهری کارمند اداره پست و تلگراف بود.<br>سهراب از هشت سالگی شعر میگفت و نقاشی میکرد.با اتمام تحصیلات مقدماتی و متوسطه در کاشان؛ دانشکده هنرهای زیبایی تهران فرصتی بود تا ذوق و استعداد هنرمند جوان را شکوفا کند.<br>سهراب با احراز مرتبه نخست و دریافت نشان درجه اول علمی؛ دانشکده را پشت سر گذاشت.<br>انتشار مجموعه اشعار (مرگ رنگ) و کمی دیرتر (زندگی خوابها) شرکت در نمایشگاه های انفرادی و گروهی و دریافت جایزه اول هنرهای زیبایی در بینال دوم تهران و کمی دیرتر انتشار دو کتاب (آواز آفتاب و شرق اندوه) فرصتی فراهم کرد تا دوستان شعر و نقاشی با ذوق هنری سهراب آشنا شوند. او اشعار کلاسیک فراوانی دارد.<br>علاوه بر اشعار کلاسیک پراکنده سهراب و تابلو های نقاشی اش درموزه های معتبر دنیا <br> (هشت بهشت) او را می توان بهترین اثر مکتوب او دانست.<br>سرانجام به تاریخ اردیبهشت سال 1359 شمسی سهراب دار فانی را وداع گفت. پیکر پاک این شاعر و هنرمند ایرانی بنا به وصیتش در مشهد اردهال کاشان به خاک سپرده شد.', 4, 1),
(32, 'علیرضا افتخاری', 'علیرضا افتخاری مهیاری (زاده ۱۰ فروردین ۱۳۳۷ در اصفهان) از خوانندگان بنام موسیقی سنتی ایرانی است. افتخاری طی چندین سال فعالیت در عرصه موسیقی سنتی ایرانی، خوانندگی بیش از شصت آلبوم موسیقی را بر عهده داشته است. وی در سال ۱۳۸۹ موفق به اخذ نشان چهره‌های ماندگار شد.<br>وی در سال ۱۳۳۷ در اصفهان دیده به جهان گشود. در کودکی نزد طباطبایی نوازنده ویولن تعلیم دید. از ۱۲ سالگی نزد استاد تاج اصفهانی به یادگیری ردیف‌های آوازی پرداخت.<br>پس از مدتی نیز همزمان به بهره‌گیری از محضر جلیل شهناز و حسن کسایی در زمینه ردیف‌های موسیقی سنتی مشغول شد. در سال ۱۳۵۵ در آزمون باربد و در محضر اساتیدی چون علی اکبر خان شهنازی، داریوش صفوت، علی تجویدی و … موفق به کسب رتبه نخست گشت. از سال ۱۳۶۰  در سال ۱۳۶۲، اولین آلبوم خود را به توصیه فرامرز پایور با نام «آتش دل» و به یاد استاد فقیدش تاج اصفهانی منتشر کرد. پس از آن، آثار او از صدا و سیمای ایران به صورت مرتب پخش شده و کنسرت‌های مختلفی را در کشورهایی چون ژاپن، آلمان، کانادا و انگلستان برگزار نموده است.', 4, 1),
(33, 'رئیس&zwnj;علی دلواری', 'رئیس‌علی دلواری (۱۲۶۱–۱۲۹۴ خورشیدی/ ۱۸۸۲–۱۹۱۵ میلادی) مبارز مشروطه‌خواه و رهبر قیام جنوب در تنگستان و بوشهر علیه نیروهای بریتانیا در دورهٔ جنگ جهانی اول بود.\r<br> باشروع نهضت مشروطیت، رئیسعلی در حالی که ۲۴ سال بیشتر نداشت از جمله پیشگامان مشروطه‌خواه در جنوب ایران شد و همکاری نزدیکی با محافل انقلابی و عناصر مشروطه‌طلب در بوشهر، تنگستان و دشتی آغاز کرد.\r<br>دوران جوانی او همزمان با حضور گسترده استعمار انگلیس در خلیج فارس بود. وی نقشی کلیدی در قیام جنوب داشت. قیام مردم تنگستان به رهبری وی روی هم هفت سال طول کشید و در این مدت دلیران تنگستانی دو هدف عمده را دنبال می‌کردند، پاسداری از بوشهر و دشتستان و تنگستان به عنوان منطقه سکونت خود و جلوگیری از حرکت قوای بیگانه به درون مرزهای ایران و دفاع از استقلال وطن.\r<br>رئیس‌علی هنگام شبیخون به قوای بریتانیا توسط فردی نفوذی به نام غلامحسین تنگکی، از پشت سر هدف گلوله قرار گرفت و در سن ۳۳ سالگی کشته شد.', 7, 1),
(34, 'منوچهر آتشی', 'منوچهر آتشی (۲ مهر ۱۳۱۰، دهرود شهرستان دشتستان استان بوشهر - ۲۹ آبان ۱۳۸۴، تهران) شاعر و مترجم معاصر ایرانی بود.\r<br>وی در سال 1310 در دهرود دشتستان بوشهر متولد شد. تحصيلات ابتدايی و دوره اول متوسطه را در كنگان، بوشهر و حومه گذراند. در سال 1330 وارد دانشسرای مقدماتی در شيراز شد و پس از فارغ التحصيلی از دانشسرای مقدماتی به استخدام آموزش و پرورش بوشهر و حومه در آمد. اولين مجموعه شعرش «آهنگ ديگر» را همزمان با تحصيل در دانشسرای عالی تهران در سال 1339 منتشر كرد.', 7, 1),
(35, 'یدالله امینی', 'یدالله امینی متخلص به مفتون و معروف به مفتون امینی شاعر نوپرداز معاصر  در روز 21 خرداد سال  1305 ه. ش. در  دهستان هوسالو از توابع شاهین دژ در آذربایجان غربی متولد شد . وی پس از انجام تحصیلات ابتدایی و متوسطه در مدارس تبریز  به دانشگاه تهران راه یافت ود رشته حقوق قضایی تحصیل کرد.\r<br>وی که در سال ۱۳۲۸ از دانشکده حقوق دانشگاه تهران تهران فارغ‌التحصیل شده است، ۳۱ سال در وزارت دادگستری خدمت کرده و در سال ۱۳۵۰ به دلیل سیاسی بودن اشعارش از سمت قضایی برکنار شد، اما با پیروزی انقلاب اسلامی، دوباره به کار خود بازگشت.\r<br>دوران اصلی شاعری مفتون امینی، پس از بازنشستگی در اواخر سال ۱۳۵۹ آغاز شد، اما شعرهای او در اغلب مجلات و روزنامه‌های معتبر ۶۰ سال اخیر انتشار یافته و علاوه بر شعر فارسی، شعر ترکی هم سروده است.\r<br> وزارت فرهنگ و ارشاد اسلامی در  مرداد 1395 به او مدال درجه یک هنری اعطا کرد.\r<br>از آثار او می توان به «دریاچه»؛ «کولاک» و «انارستان» اشاره کرد.', 8, 1),
(36, 'جلال آل احمد', 'جلال آل‌احمد (آذر ۱۳۰۲ - شهریور ۱۳۴۸) روشنفکر، نویسنده، منتقد ادبی و مترجم ایرانی و همسر سیمین دانشور بود. آل احمد در دهه ۱۳۴۰ به شهرت رسید و تأثیر بزرگی در جریان روشنفکری و نویسندگی ایران داشت.\r<br> دوران کودکی و نوجوانی جلال در نوعی رفاه اشرافی روحانیت گذشت. پس از اتمام دوران دبستان، پدر جلال، سید احمد طالقانی، به او اجازه درس خواندن در دبیرستان را نداد؛ اما او تسلیم خواست پدر نشد.\r<br>جلال آل‌احمد در ۱۸ شهریور ۱۳۴۸ در چهل و پنج سالگی در اَسالِم گیلان درگذشت. پس از مرگ نابهنگام آل‌احمد، پیکر وی به‌سرعت تشییع و به خاک سپرده شد، که باعث باوری دربارهٔ سربه‌نیست شدن او توسط ساواک شد.\r<br>پیکر او در مسجد فیروزآبادی جنب بیمارستان فیروزآبادی شهر ری به امانت گذاشته شد تا بعدها آرامگاهی در شأن او ایجاد شود و این کار هیچ‌گاه صورت نگرفت.', 8, 1),
(37, 'سیمین دانشور', 'سیمین دانشور (زاده ۸ اردیبهشت ۱۳۰۰ در شیراز - ۱۸ اسفند سال ۱۳۹۰ خورشیدی در تهران) نویسنده و مترجم ایرانی و همسر جلال آل‌احمد بود و در اکثر فعالیت‌های فرهنگی و اجتماعی همسرش حضور و همکاری داشت. وی نخستین زن ایرانی بود که به صورتی حرفه‌ای در زبان فارسی داستان نوشت. مهم‌ترین اثر او رمان سووشون است که نثری ساده دارد و به ۱۷ زبان ترجمه شده‌است.سووشون از جمله پرفروش‌ترین آثار ادبیات داستانی در ایران به‌شمار می‌رود. دانشور همچنین عضو و نخستین رئیس کانون نویسندگان ایران بود.‍‍', 8, 1),
(38, 'سید روح الله خاتمی', 'سید روح الله خاتمی روحانی و فقیه شیعه، موسس مدرسه علمیه اردکان و نماینده روح‌الله خمینی در استان یزد و امام جمعه شهر یزد بود. وی پدر سید محمد خاتمی رییس جمهور سابق ایران (از سال ۱۳۷۶ تا ۱۳۸۴) بود.\r<br>آیت الله سید روح الله خاتمی شامگاه دوشنبه دهم رمضان سال ۱۳۲۴ قمری در اردکان دیده به جهان گشود و با همنشینی پدر و مادر خود توانست فارسی و عربی را به خوبی بیاموزد و از سیزده سالگی نیز به عنوان طلبه به آموختن علوم دینی در زادگاه خود تحت نظر پدر پرداخت.\r<br>سید روح الله خاتمی تحصیلات تکمیلی خود را در حوزه علمیه اصفهان به سرانجام رسانید و از فشارکی اجازه اجتهاد گرفت. حاج میرزا علی آقای شیرازی، شیخ محمد خوانساری، سید محمد نجف‌آبادی و حاج رحیم ارباب از جمله استادان او بودند.\r<br>وی در اردکان، به فعالیت‌های مذهبی و سیاسی در مخالفت با حکومت رضاشاه پرداخت و در کنار آن، هیچگاه از مبارزه با تحجر و مقدس مآبی غافل نشد.\r<br> هنگامی که نخستین جرقه‌های انقلاب اسلامی با سخنان امام خمینی در سال ۱۳۴۱ شمسی شعله‌ور شد، به وی پیوست و فعالیت سیاسی خود را شدت بخشید و یکی از ارکان انقلاب اسلامی را در یزد و اردکان پی افکند.', 31, 1),
(39, 'شیخ علی بروجنی', 'حاج شیخ علی بروجنی فرزند حاج ملاّ محمّدباقر، عالم فاضل، محقّق جلیل، در حدود سال 1265 هجری قمری متولّد و در اصفهان و نجف تحصیل نموده و پس از مراجعت به اصفهان سال ها در مدرسه الماسیّه ساکن و به تدریس فقه و اصول پرداخته است. او مجتهدی بارع و محقّقی کامل بود. وی در اواخر عمر به موطن خویش مراجعت نموده و حدود سال 1330 قمری در آنجا وفات یافته است.', 9, 1),
(40, 'آیت الله سید محمد رضا بختیاری', 'آیت الله حاج سیّد محمّد رضا بختیاری فرزند سیّد محمّد بن سیّد محمّد تقی، عالم فاضل معاصر در سال 1304 هجری قمری در چهارمحال و بختیاری متولّد شد و پس از کمی تحصیل به اصفهان رفته و نزد شیخ محمّد تقی آقانجفی ادامه تحصیل داد. سپس به عتبات عالیات عزیمت کرده از محضر آیات عظام سیّد محمّد کاظم طباطبایی یزدی، آخوند ملّا محمّد کاظم خراسانی و میرزا محمّد تقی شیرازی بهره برد و به اجتهاد نائل آمد.    \r<br>پس از قیام عشایر و مردم علیه سلطه انگلیسی ها بر عراق و فتوای جهاد علما، او به مجاهدان پیوست و فرماندهی شصت هزار نفر از آنان را برعهده گرفت. پس از پایان جنگ به ایران مراجعت کرده و در مشهد مقدس ساکن شد و به تدریس و اقامه جماعت مشغول شد و مورد وثوق حاج آقا حسین قمی واقع شد.\r<br>وی پس از کشتار مردم و سرکوب قیام مسجد گوهرشاد به بیرجند تبعید شد و پس از شهریور 1320 به مشهد بازگشت و به تدریس پرداخت تا اینکه در روز یکشنبه 11 اردیبهشت 1339 وفات یافت و در یکی از حجره های سمت راست نقاره خانه (صحن انقلاب کنونی) مدفون شد.', 9, 1);
